import { useState, useEffect } from 'react'
import personService from './services/persons'

const Content = ({parts, delFunc}) => {
  return (
    <>
      {parts.map(part =>
        <Person key={part.name} parts={part} delFunc={delFunc}  />
      )}
    </>
  )
}

const Person = ({parts, delFunc}) => {
  const myFunction = delFunc
  return (
    <>
      <form onSubmit={myFunction(parts.id)}>
        <>{parts.name} {parts.number} </>
        <button type="submit">delete</button>
      </form>
    </>
  )
}

const App = () => {
  const [persons, setPersons] = useState([]) 
  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('')

  useEffect(() => {
    personService
      .getAll()
      .then(initialPersons => {
        setPersons(initialPersons)
      })
      .catch(error => {
        console.log("fail", error)
      })
  }, [])

  const addPerson = (event) => {
    event.preventDefault()
    if (persons.find(persons => persons.name === newName) === undefined) {
      const personObject = {
        name: newName,
        number: newNumber,
      }

      personService
        .create(personObject)
        .then(returnedPerson => {
          setPersons(persons.concat(returnedPerson))
          setNewName('')
          setNewNumber('')
        })
        .catch(error => {
          console.log("fail", error)
        })
    }
    else
    {
      alert(`${newName} is already added to phonebook`)
    }
  }

  const deletePerson = (id) => {
    console.log(id)
    if (persons.find(persons => persons.id === id) !== undefined) {
      const person = persons.find(p => p.id === id)
      const deletedPerson = { ...person}

      personService
        .update(id, deletedPerson)
        .then(returnedPerson => {
          setPersons(persons.filter(p => p.id !== id))
        })
        .catch(error => {
          console.log("fail", error)
        })
    }
    else
    {
      alert(`${id} is already deleted from server`)
    }
  }

  const handlePersonChange = (event) => {
    setNewName(event.target.value)
  }

  const handleNumberChange = (event) => {
    setNewNumber(event.target.value)
  }

  return (
    <div>
      <h2>Phonebook</h2>
      <form onSubmit={addPerson}>
        <>name: <input value={newName} onChange={handlePersonChange} /></><br></br>
        <>number: <input value={newNumber} onChange={handleNumberChange} /></><br></br>
        <button type="submit">add</button>
      </form>
      <h2>Numbers</h2>
      <Content parts={persons} delFunc={deletePerson} />
    </div>
  )

}

export default App
